<style>
  .category-section {
    width: 100%;
    background-color: #e0f4ff;
    padding: 40px 0;
    box-sizing: border-box;
  }

  .category-container {
    max-width: 1300px;
    margin: 0 auto;
    display: flex;
    justify-content: space-between;
    gap: 20px;
    margin-left: 220px;
    margin-right: 220px;
    position: relative;
  }

  .logo-category {
    position: absolute;
    top: 0;
    left: 0;
    display: flex;
  }

  .logo-category p {
    margin-top: 6px;
    margin-left: 5px;
    font-family: 'Roboto';
    font-size: 14px;
    color: #00AAFF;
  }

  .text-category {
    position: absolute;
    top: 40px;
    left: 0;
    display: flex;
  }

  .text-category h2 {
    font-weight: bold;
    font-family: 'Roboto';
    font-size: 25px;
    color: #333;
  }

  .text-category2 {
    margin-top: 80px;
    max-width: 530px;
  }

  .text-category2 p {
    font-family: 'Roboto';
    font-size: 16px;
    color: #000000;
    line-height: 1.6;
    margin: 0;
  }

  /* Style for the button */
  .btn-try {
    margin-top: 15px;
    display: inline-flex;
    justify-content: center;
    align-items: center;
    padding: 8px 10px;
    background: linear-gradient(to right, #21BEE8 0%, #00AAFF 100%);
    border: none;
    border-radius: 5px;
    cursor: pointer;
    font-family: 'Roboto', sans-serif;
    transition: transform 0.2s ease;
    margin-left: 220px;
  }

  .btn-try p {
    margin: 0;
    font-size: 12px;
    font-weight: bold;
    color: white;
  }

  .btn-try:hover {
    transform: translateY(-3px);
  }


  .btn-try:focus {
    outline: none;
    box-shadow: 0 0 0 2px #00AAFF;
  }
</style>

